// --- Config from Config Node ---
// This line was corrected: References 'Config' instead of 'Globals'
const config = $('Config').item.json;
const minTemp = config.minTemp;
const maxTemp = config.maxTemp;
const allowedConditionsInput = config.allowedConditions || 'Clear,Clouds';
const allowedList = allowedConditionsInput.split(',').map(s => s.trim().toLowerCase());

// --- Forecast Data & City Info from Get Weather Forecast Node ---
// Expects input to be the direct result of the OpenWeatherMap API call
const forecastData = $input.item.json;
const forecasts = forecastData.list || [];
const cityInfo = forecastData.city || {}; // Get city info for sunrise/sunset
const sunrise = cityInfo.sunrise; // Unix timestamp
const sunset = cityInfo.sunset; // Unix timestamp

const suitable = [];
const daysDone = new Set();

console.log(`Filtering Weather: Temp=${minTemp}-${maxTemp}Â°C, Conditions=${allowedList.join(', ')}`);

for (const f of forecasts) {
  const date = new Date(f.dt * 1000);
  const day = date.toISOString().split('T')[0]; // YYYY-MM-DD
  const hour = date.getHours();
  const temp = f.main.temp;
  const condition = (f.weather?.[0]?.main || '').toLowerCase();
  const pressure = f.main.pressure;
  const wind_speed = f.wind?.speed;
  const wind_deg = f.wind?.deg;

  if (daysDone.has(day)) continue;
  if (hour < 8 || hour >= 18) continue; // Only daytime (8 AM to 5:59 PM)
  if (temp < minTemp || temp > maxTemp) continue; // Check temperature
  if (!allowedList.includes(condition)) continue; // Check weather condition

  console.log(`--> Suitable slot found for day ${day} at ${date.toISOString()}`);
  suitable.push({
    json: { // Return extended data for each suitable slot
      date: day,
      dt: f.dt, // Unix timestamp for the specific forecast slot
      temp,
      condition: f.weather?.[0]?.description || condition,
      pressure,
      wind_speed,
      wind_deg,
      sunrise, // Include sunrise/sunset from city info
      sunset
    }
  });
  daysDone.add(day); // Mark day as processed
}

console.log(`Found ${suitable.length} suitable day(s) based on weather.`);
return suitable; // Return array of suitable slots with extended data

