// --- Get Calendar Events ---
// Input 0: Calendar events from Check Duty Roster
const calendarEventItems = items; 
console.log(`Received ${calendarEventItems.length} calendar event items.`);

// --- Define Date Range (Next 14 days) ---
const startDate = new Date();
startDate.setHours(0, 0, 0, 0); // Start of today
const endDate = new Date(startDate);
endDate.setDate(startDate.getDate() + 14); // End of the 14th day
console.log(`Checking for free days between ${startDate.toISOString().split('T')[0]} and ${endDate.toISOString().split('T')[0]}`);

// --- Create Set of Occupied Dates ---
const occupiedDates = new Set();
for (const eventItem of calendarEventItems) {
  if (!eventItem || !eventItem.json || !eventItem.json.start) {
    console.warn("Skipping invalid calendar event item:", eventItem);
    continue;
  }
  const event = eventItem.json;
  const eventDateString = event.start.date || event.start.dateTime;
  if (!eventDateString) {
     console.warn("Skipping calendar event item with missing date/dateTime:", event);
     continue;
  }
  const eventDate = eventDateString.substring(0, 10); // YYYY-MM-DD
  occupiedDates.add(eventDate);
}
console.log(`Found ${occupiedDates.size} unique occupied dates in the fetched range.`);

// --- Find Free Dates in Range ---
const freeDays = [];
let currentDate = new Date(startDate);

while (currentDate < endDate) {
  const currentDateStr = currentDate.toISOString().split('T')[0]; // YYYY-MM-DD
  if (!occupiedDates.has(currentDateStr)) {
    console.log(`--> Day ${currentDateStr} is free.`);
    freeDays.push({ json: { freeDate: currentDateStr } });
  }
  // Move to the next day
  currentDate.setDate(currentDate.getDate() + 1);
}

console.log(`Identified ${freeDays.length} free days in the next 14 days.`);

// Return array of free dates
return freeDays;

// This script calculates a simple fishing bite index based on weather data.
// Input: Expects an array of items, each item.json contains weather data for a suitable slot
// from the Filter Weather node (including dt, pressure, wind_speed, sunrise, sunset).
// Output: Returns the input array, with an added 'beissindex' (0-10) property in each item.json.

function calculateBiteIndex(weatherItems) {
  if (!weatherItems || weatherItems.length === 0) {
    console.log("No weather items received to calculate Bite Index.");
    return [];
  }

  for (const item of weatherItems) {
    const data = item.json;
    let score = 5; // Start with a baseline score

    // Simple heuristic checks - adjust points as needed
    console.log(`Calculating index for date: ${data.date}, time: ${new Date(data.dt * 1000).toISOString()}`);

    // 1. Pressure Check
    const pressure = data.pressure;
    if (pressure) {
        if (pressure >= 1010 && pressure <= 1022) score += 1; // Stable 'normal' pressure
        else if (pressure < 1005 || pressure > 1028) score -= 1; // Very low or high pressure often less ideal
        console.log(`  - Pressure (${pressure} hPa): Score adjusted to ${score}`);
    }

    // 2. Wind Speed Check (m/s)
    const windSpeed = data.wind_speed;
    if (windSpeed !== undefined) {
        if (windSpeed > 1.5 && windSpeed < 7) score += 1; // Light to moderate breeze often good (approx 5-25 km/h)
        else if (windSpeed >= 7) score -= 1; // Strong wind often less ideal
        console.log(`  - Wind Speed (${windSpeed} m/s): Score adjusted to ${score}`);
    }

    // 3. Time of Day Check (relative to sunrise/sunset)
    const dt = data.dt;
    const sunrise = data.sunrise;
    const sunset = data.sunset;
    if (dt && sunrise && sunset) {
        const hoursAfterSunrise = (dt - sunrise) / 3600;
        const hoursBeforeSunset = (sunset - dt) / 3600;
        // Check if within ~2 hours of sunrise or sunset
        if ((hoursAfterSunrise >= -0.5 && hoursAfterSunrise <= 2) || (hoursBeforeSunset >= -0.5 && hoursBeforeSunset <= 2)) {
            score += 2; // Prime bite times
            console.log(`  - Time (Dawn/Dusk): Score adjusted to ${score}`);
        }
    }

    // 4. Weather Condition (already filtered, but cloudy/overcast is often preferred)
    const conditionDesc = (data.condition || '').toLowerCase();
    if (conditionDesc.includes('cloud') || conditionDesc.includes('overcast') || conditionDesc.includes('mist') || conditionDesc.includes('fog') || conditionDesc.includes('drizzle')) {
      score += 1; // Slightly favor overcast/low light
      console.log(`  - Condition (${conditionDesc}): Score adjusted to ${score}`);
    }

    // Normalize score to 0-10 range
    score = Math.max(0, Math.min(10, Math.round(score)));
    console.log(`  - Final Score: ${score}/10`);

    item.json.beissindex = score;
  }

  return weatherItems;
}

// Assuming the input comes directly from the previous node's items
const inputItems = items; 
return calculateBiteIndex(inputItems);
