// isDiffOrNew.js - Defensive Version 3

// Initialize the object that will eventually be returned
let resultItem = { json: {} }; // Start with an empty json object

// --- Get GitHub Data (items[0]) ---
let githubData = null;
if (items[0] && items[0].json) {
  githubData = items[0].json;
  // Copy over initial GitHub data/error to resultItem for context
  Object.assign(resultItem.json, githubData);
} else {
  console.error("Input item from GitHub (items[0]) is missing or has no json property.");
  resultItem.json.github_status = "error_missing_github_input";
  // Attempt to process n8n data anyway for a 'new' push? Or just return error?
  // Let's try to continue and see if we can determine 'new' status.
}

// --- Get Sanitized n8n Data (items[1]) ---
let n8nWorkflow = null;
if (items[1] && items[1].json && typeof items[1].json === 'object' && items[1].json !== null) {
  n8nWorkflow = items[1].json; // Direct access assumed after sanitize node update
} else {
  console.error("Input item from Sanitize (items[1]) is missing, has no json, or json is not a valid object.");
  resultItem.json.github_status = resultItem.json.github_status || "error_missing_n8n_input"; // Keep previous error if any
  // Can't proceed without n8n data
  return [resultItem];
}

// --- Extract Workflow Name ---
// Safely try to get the workflow name and add it to the result
if (n8nWorkflow && n8nWorkflow.name) {
  resultItem.json.workflowName = n8nWorkflow.name; // ADDED workflowName
} else {
  console.warn("Could not find workflow name in sanitized n8n data. Using fallback name.");
  resultItem.json.workflowName = `workflow_${n8nWorkflow.id || 'unknown'}`; // Fallback name using ID
}

// --- Determine Status (Compare or New) ---

// CASE 1: GitHub file exists (has 'content')
if (githubData && typeof githubData.content === 'string') {
  let origWorkflow;
  try {
    origWorkflow = JSON.parse(Buffer.from(githubData.content, 'base64').toString());
    if (typeof origWorkflow !== 'object' || origWorkflow === null) {
       throw new Error("Parsed GitHub content is not an object.");
    }

    // --- Order and Compare ---
    var orderedOriginal = {};
    var orderedActual = {}; // Will hold ordered n8n data

    Object.keys(origWorkflow).sort().forEach(function(key) {
      orderedOriginal[key] = origWorkflow[key];
    });

    // Check n8nWorkflow again before keys (should be object from above)
    if (typeof n8nWorkflow !== 'object' || n8nWorkflow === null) {
       throw new Error("n8nWorkflow became invalid before comparison keys.");
    }
    Object.keys(n8nWorkflow).sort().forEach(function(key) {
      orderedActual[key] = n8nWorkflow[key];
    });

    // Perform comparison
    if (JSON.stringify(orderedOriginal) === JSON.stringify(orderedActual)) {
      resultItem.json.github_status = "same";
      // No need for n8n_data_stringy if same
      delete resultItem.json.n8n_data_stringy;
      resultItem.json.content_decoded = orderedOriginal; // Optional: keep decoded original
    } else {
      resultItem.json.github_status = "different";
      resultItem.json.n8n_data_stringy = JSON.stringify(orderedActual, null, 2); // Stringify the ordered n8n data
      resultItem.json.content_decoded = orderedOriginal; // Optional: keep decoded original
    }

  } catch (e) {
    console.error("Error during GitHub content parsing or comparison:", e);
    resultItem.json.github_status = "error_comparing";
    // If comparison fails, maybe still push the sanitized n8n data?
    try {
        if (typeof n8nWorkflow === 'object' && n8nWorkflow !== null) {
            var orderedActualOnError = {};
             Object.keys(n8nWorkflow).sort().forEach(function(key) { orderedActualOnError[key] = n8nWorkflow[key]; });
             resultItem.json.n8n_data_stringy = JSON.stringify(orderedActualOnError, null, 2);
        } else {
             delete resultItem.json.n8n_data_stringy;
        }
    } catch (stringifyError) {
        console.error("Error stringifying n8n data even after comparison error:", stringifyError);
         delete resultItem.json.n8n_data_stringy;
    }
  }

// CASE 2: GitHub file does NOT exist (no 'content' in githubData or githubData itself was null)
} else {
  console.log("GitHub file not found or no content key. Processing as 'new'.");
  try {
    // Check n8nWorkflow again (should be object from above)
     if (typeof n8nWorkflow !== 'object' || n8nWorkflow === null) {
       throw new Error("n8nWorkflow is invalid for 'new' file processing.");
    }

    var orderedActualNew = {};
    Object.keys(n8nWorkflow).sort().forEach(function(key) {
      orderedActualNew[key] = n8nWorkflow[key];
    });

    resultItem.json.github_status = "new";
    resultItem.json.n8n_data_stringy = JSON.stringify(orderedActualNew, null, 2); // Stringify the ordered n8n data

  } catch (e) {
    console.error("Error during processing for 'new' file:", e);
    resultItem.json.github_status = "error_processing_new";
    delete resultItem.json.n8n_data_stringy; // Don't include potentially corrupt data
  }
}

// Return the single result item
return [resultItem];

