// Sanitize.js - More Dynamic Version

// --- Configuration ---
// List of sensitive SUBSTRINGS (lowercase) to detect within parameter keys recursively
const SENSITIVE_SUBSTRINGS = [
    'key',       // Catches apikey, secretkey, privatekey, etc.
    'token',     // Catches apitoken, accesstoken, refreshtoken, etc.
    'secret',    // Catches clientsecret, etc.
    'password', 'pass' // Catches password, dbpassword, etc.
    // Add more generic substrings if needed, but be careful not to be too broad
];

// List of sensitive keys directly on the node object (outside parameters)
const SENSITIVE_NODE_KEYS = [
    'webhookId' // Add others if needed, e.g., 'botToken'
];

// Regex patterns for sensitive string values
const PATTERNS = {
    PRIVATE_IP: /(?:(?:\b(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\b(?:[:\/\?]|$))|(?:\b(?:10\.(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){2}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)|172\.(?:1[6-9]|2[0-9]|3[0-1])\.(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){1}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)|192\.168\.(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){1}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?))\b(?:[:\/\?]|$)))/g,
    GSHEET_ID: /(?:\/d\/|value=)([a-zA-Z0-9_-]{40,})/g, // Google Sheet ID (long alphanumeric)
    ABS_PATH: /(\/data\/|\/home\/|\/Users\/|\/mnt\/|\/media\/|\/var\/log\/|C:\\Users\\|C:\\Program Files\\)[^\'"\s\<]+/g, // Absolute Paths (common OS roots, avoiding already sanitized)
    WEBHOOK_URL: /(https?:\/\/[^\s\'\"]*(\/webhook\/|\/webhook-test\/)[^\s\'\"]*)/gi, // URLs with /webhook/ or /webhook-test/
    EMAIL: /(['\"]?)([A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,})(['\"]?)/g, // Email addresses (basic)
    GOOGLE_CALENDAR_ID_PATTERN: /(@group\.calendar\.google\.com|@import\.calendar\.google\.com|\.calendar\.google\.com)/ // Specific check for calendar IDs
};

const MAX_RECURSION_DEPTH = 10; // Prevent infinite loops in circular structures

// --- Helper Functions ---

/**
 * Recursively sanitizes an object or array based on key substrings and value patterns.
 * @param {any} data - The object or array to sanitize.
 * @param {string} parentKey - The key of the parent object holding this data.
 * @param {number} currentDepth - The current recursion depth.
 * @returns {any} The sanitized data.
 */
function sanitizeObjectRecursive(data, parentKey = '', currentDepth = 0) {
    if (currentDepth > MAX_RECURSION_DEPTH) return data;

    if (Array.isArray(data)) {
        return data.map(item => sanitizeObjectRecursive(item, parentKey, currentDepth + 1));
    } else if (data !== null && typeof data === 'object') {

        // Handle Set Node Structure specifically first
        if (parentKey === 'values' && data.string && Array.isArray(data.string)) {
            const sanitizedStrings = data.string.map(item => {
                let newItem = { ...item }; // Clone item
                if (newItem && newItem.name && typeof newItem.value === 'string' && !newItem.value.startsWith('<<')) {
                    const lowerName = newItem.name.toLowerCase();
                    // Check if item name contains sensitive substring
                    if (SENSITIVE_SUBSTRINGS.some(sub => lowerName.includes(sub))) {
                        newItem.value = `<<SET_${newItem.name.toUpperCase()}_REMOVED>>`;
                    } else {
                         // If key not sensitive, still check value pattern
                         newItem.value = sanitizeStringValueByPattern(newItem.value);
                    }
                }
                return newItem;
            });
            return { ...data, string: sanitizedStrings };
        }

        // Default Object Handling
        const sanitizedObj = {};
        for (const key in data) {
            if (data.hasOwnProperty(key)) {
                const lowerKey = key.toLowerCase();
                const value = data[key];
                let processedValue = null;

                // 1. Check if the key contains a sensitive substring
                if (SENSITIVE_SUBSTRINGS.some(sub => lowerKey.includes(sub))) {
                    if (typeof value === 'string' && value.length > 3 && !value.startsWith('<<')) {
                        processedValue = `<<${key.toUpperCase()}_REMOVED>>`;
                    } else if (typeof value === 'object' && value !== null && value.hasOwnProperty('value') && typeof value.value === 'string' && value.value.length > 3 && !value.value.startsWith('<<')) {
                        processedValue = { ...value, value: `<<${key.toUpperCase()}_RL_REMOVED>>` };
                    }
                    // Add handling for other types (like numbers?) if needed, though unlikely for keys
                }

                // 2. If not processed by key check, sanitize value based on type
                if (processedValue === null) {
                    if (typeof value === 'string' && !value.startsWith('<<')) {
                        processedValue = sanitizeStringValueByPattern(value);
                        // If pattern didn't modify, keep original for potential recursion
                        if (processedValue === value) processedValue = null;
                    } else if (typeof value === 'object' && value !== null) {
                        processedValue = sanitizeObjectRecursive(value, key, currentDepth + 1);
                    }
                }

                sanitizedObj[key] = processedValue !== null ? processedValue : value;
            }
        }
        return sanitizedObj;
    } else {
        return data;
    }
}

/**
 * Applies predefined regex patterns to sanitize a string value.
 * @param {string} value The string to sanitize.
 * @returns {string} The sanitized string or the original if no patterns matched.
 */
function sanitizeStringValueByPattern(value) {
    if (typeof value !== 'string' || value.startsWith('<<')) return value;

    let sanitizedValue = value;
    let modified = false;

    const skipEmailPattern = PATTERNS.GOOGLE_CALENDAR_ID_PATTERN.test(value);

    if (PATTERNS.PRIVATE_IP.test(sanitizedValue)) {
        PATTERNS.PRIVATE_IP.lastIndex = 0;
        sanitizedValue = sanitizedValue.replace(PATTERNS.PRIVATE_IP, '<<PRIVATE_IP_REMOVED>>');
        modified = true;
    }
    if (PATTERNS.GSHEET_ID.test(sanitizedValue)) {
        PATTERNS.GSHEET_ID.lastIndex = 0;
        sanitizedValue = sanitizedValue.replace(PATTERNS.GSHEET_ID, (match, p1) => match.replace(p1, '<<GOOGLE_SHEET_ID_REMOVED>>'));
        modified = true;
    }
    if (PATTERNS.ABS_PATH.test(sanitizedValue)) {
        PATTERNS.ABS_PATH.lastIndex = 0;
        sanitizedValue = sanitizedValue.replace(PATTERNS.ABS_PATH, '<<ABSOLUTE_FILE_PATH_REMOVED>>');
        modified = true;
    }
    if (PATTERNS.WEBHOOK_URL.test(sanitizedValue)) {
        PATTERNS.WEBHOOK_URL.lastIndex = 0;
        sanitizedValue = sanitizedValue.replace(PATTERNS.WEBHOOK_URL, '<<WEBHOOK_URL_REMOVED>>');
        modified = true;
    }
    if (!skipEmailPattern && PATTERNS.EMAIL.test(sanitizedValue) && sanitizedValue.includes('@') && sanitizedValue.includes('.') && !sanitizedValue.includes('://')) {
        PATTERNS.EMAIL.lastIndex = 0;
        sanitizedValue = sanitizedValue.replace(PATTERNS.EMAIL, (match, p1, p2, p3) => `${p1||''}<<EMAIL_REMOVED>>${p3||''}`);
        modified = true;
    }

    return modified ? sanitizedValue : value;
}

/**
 * Sanitizes jsCode within Code/Function nodes.
 * @param {string} code - The original JavaScript code.
 * @returns {string} The sanitized code.
 */
function sanitizeJsCode(code) {
    if (typeof code !== 'string') return code;

    let modifiedCode = code;
    let sanitized = false;

    const keyPattern = /(api[_-]?key|secret|token|password|pass)(\s*[:=]\s*['\"])([^'\"\s]+)(['\"])/gi;
    modifiedCode = modifiedCode.replace(keyPattern, (match, keyName, prefix, originalValue, suffix) => {
        if (!originalValue.startsWith('<<') && originalValue.length > 5) {
            sanitized = true;
            return `${keyName}${prefix}<<${keyName.toUpperCase()}_IN_CODE_REMOVED>>${suffix}`;
        }
        return match;
    });

    modifiedCode = modifiedCode.replace(PATTERNS.EMAIL, (match, prefixQuote, emailFound, suffixQuote) => {
        const lineStartMatch = modifiedCode.substring(0, modifiedCode.indexOf(match)).match(/^(.*)$/gm);
        const currentLine = lineStartMatch ? lineStartMatch[lineStartMatch.length - 1] : '';
        const isCommented = /^\s*(\/\/|#|\/\*|\*)/.test(currentLine);

        if (!isCommented && emailFound.includes('@') && emailFound.includes('.') && !emailFound.startsWith('<<') && !match.includes('://')) {
            sanitized = true;
            return `${prefixQuote || ''}<<EMAIL_IN_CODE_REMOVED>>${suffixQuote || ''}`;
        }
        return match;
    });

    return sanitized ? modifiedCode : code;
}

// --- Main Script Logic ---

const workflowItem = $input.item;
const workflowData = workflowItem.json;

if (!workflowData || !workflowData.nodes) {
  return workflowItem; // Return original if structure is invalid
}

// Create a deep copy to modify safely
const sanitizedWorkflow = JSON.parse(JSON.stringify(workflowData));

// 1. Sanitize Meta Data
if (sanitizedWorkflow.meta) {
  const metaKeysToRemove = ['instanceId', 'templateCredsSetupCompleted', 'secrets'];
  for (const key of metaKeysToRemove) {
      if (sanitizedWorkflow.meta.hasOwnProperty(key)) {
          delete sanitizedWorkflow.meta[key];
      }
  }
}

// 2. Sanitize Nodes
if (Array.isArray(sanitizedWorkflow.nodes)) {
    for (const node of sanitizedWorkflow.nodes) {
        // a. Remove entire credentials block
        if (node.credentials) {
            delete node.credentials;
        }

        // b. Sanitize sensitive keys directly on the node object
        for (const nodeKey of SENSITIVE_NODE_KEYS) {
             if (node.hasOwnProperty(nodeKey) && typeof node[nodeKey] === 'string' && node[nodeKey].length > 5 && !node[nodeKey].startsWith('<<')) {
                 node[nodeKey] = `<<NODE_${nodeKey.toUpperCase()}_REMOVED>>`;
             }
        }

        // c. Recursively sanitize parameters first (using substring checks)
        if (node.parameters && typeof node.parameters === 'object') {
            node.parameters = sanitizeObjectRecursive(node.parameters);
        }

        // d. Node-specific structural/override sanitization *after* recursion

        // Override Google Calendar ID
        if (node.type === 'n8n-nodes-base.googleCalendar' && node.parameters?.calendar?.value && typeof node.parameters.calendar.value === 'string') {
           const calId = node.parameters.calendar.value;
           if (!calId.startsWith('<<GOOGLE_CALENDAR_ID') && PATTERNS.GOOGLE_CALENDAR_ID_PATTERN.test(calId) ) {
             const calendarName = node.parameters.calendar.cachedResultName || 'GOOGLE';
             node.parameters.calendar.value = `<<GOOGLE_CALENDAR_ID_FOR_${calendarName.replace(/[^a-zA-Z0-9]/g, '_').toUpperCase()}_REMOVED>>`;
          }
        }

        // Ensure Gmail Label IDs use the specific placeholder
        if (node.type === 'n8n-nodes-base.gmail' && node.parameters?.filters?.labelIds && Array.isArray(node.parameters.filters.labelIds)) {
            node.parameters.filters.labelIds = node.parameters.filters.labelIds.map(labelId => {
                if (typeof labelId === 'string' && labelId.startsWith('Label_') && labelId !== '<<GMAIL_LABEL_ID_REMOVED>>') {
                     return '<<GMAIL_LABEL_ID_REMOVED>>';
                }
                return labelId;
            });
        }

         // Sanitize jsCode
        if ((node.type === 'n8n-nodes-base.code' || node.type === 'n8n-nodes-base.function') && node.parameters?.jsCode) {
           node.parameters.jsCode = sanitizeJsCode(node.parameters.jsCode);
        }

        // Ensure SSH command paths use the specific placeholder
         if (node.type === 'n8n-nodes-base.ssh' && node.parameters?.command && typeof node.parameters.command === 'string') {
             // Check if general path pattern hit it, and override
             if (node.parameters.command.includes('<<ABSOLUTE_FILE_PATH_REMOVED>>')) {
                 node.parameters.command = node.parameters.command.replace(/<<ABSOLUTE_FILE_PATH_REMOVED>>/g, '<<SSH_COMMAND_PATH_REMOVED>>');
             }
             // Also apply if the general pattern missed it somehow but it contains a path
             else if (!node.parameters.command.startsWith('<<') && PATTERNS.ABS_PATH.test(node.parameters.command)) {
                PATTERNS.ABS_PATH.lastIndex = 0;
                node.parameters.command = node.parameters.command.replace(PATTERNS.ABS_PATH, '<<SSH_COMMAND_PATH_REMOVED>>');
             }
         }

    }
} else {
     // console.warn("Warning: sanitizedWorkflow.nodes is not an array.");
}

// Replace original json data with the sanitized version
workflowItem.json = sanitizedWorkflow;

return workflowItem;
