// Mapping of start-end times to shift codes
const shiftCodes = {
    '07:35-15:50': 'F',
    '08:30-16:45': 'F1',
    '09:00-17:15': 'F2',
    '09:30-17:45': 'F3',
    '10:00-18:15': 'M1',
    '11:00-19:15': 'M2',
    '11:35-19:50': 'M3', // This is the key for potential MO merge
    // B36, B38, MO are handled by specific logic below
};

// Function to parse the PDF text content
function parseTimeSheet(pdfData) {
    const text = pdfData.text;
    const lines = text.split('\n');
    const mainEntries = []; // Store regular shifts, vacation, holidays
    const bereitschaftEntries = []; // Store all entries from Bereitschaftsdienste section
    let currentYear = '';
    let currentMonth = '';

    // Regex patterns
    const monthYearRegex = /Abrechnungsmonat (\d{2})\/(\d{4})/;
    const shiftRegex = /^(\d{2})\s+\w+\s+KO\*\s+(\d{2}:\d{2})\s+GE\*\s+(\d{2}:\d{2})/;
    const vacationRegex = /^(\d{2})\s+\w+\s+URLTV/;
    const holidayRegex = /^(\d{2})\s+\w+\*?\s+FEIER/;
    const onCallBereitschaftRegex = /^(\d{2}\.\d{2}\.\d{4})\s+.*?(\d{2}:\d{2})\s+(\d{2}:\d{2})/;
    const bereitSectioRegex = /Bereitschaftsdienste/;

    let inBereitschaftSection = false;

    // --- Pass 1: Extract month/year and populate initial lists ---
    for (const line of lines) {
        // Find month and year first
        if (!currentYear || !currentMonth) {
            const monthYearMatch = line.match(monthYearRegex);
            if (monthYearMatch) {
                currentMonth = monthYearMatch[1];
                currentYear = monthYearMatch[2];
                console.log(`Found Year: ${currentYear}, Month: ${currentMonth}`);
            }
        }

        // Check for section change
        if (bereitSectioRegex.test(line)) {
            inBereitschaftSection = true;
            continue;
        }

        if (inBereitschaftSection) {
            const onCallMatch = line.match(onCallBereitschaftRegex);
            if (onCallMatch) {
                const [_, dateStr, startTime, endTime] = onCallMatch;
                const [day, month, year] = dateStr.split('.');
                // Ensure year consistency if PDF spans year boundary (unlikely for monthly sheet)
                const entryYear = year; // Use year from the line
                const fullDate = `${entryYear}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;
                bereitschaftEntries.push({ date: fullDate, start: startTime, end: endTime });
            }
        } else {
            // Process main time log section (before Bereitschaftsdienste)
            const shiftMatch = line.match(shiftRegex);
            const vacationMatch = line.match(vacationRegex);
            const holidayMatch = line.match(holidayRegex);

            if (shiftMatch && currentYear && currentMonth) {
                const [_, day, startTime, endTime] = shiftMatch;
                const date = `${currentYear}-${currentMonth.padStart(2, '0')}-${day.padStart(2, '0')}`;
                const timeKey = `${startTime}-${endTime}`;
                const shiftType = shiftCodes[timeKey] || `‚ö†Ô∏è ${timeKey}`;
                mainEntries.push({ type: shiftType, date: date, start: startTime, end: endTime });
            } else if (vacationMatch && currentYear && currentMonth) {
                const [_, day] = vacationMatch;
                const date = `${currentYear}-${currentMonth.padStart(2, '0')}-${day.padStart(2, '0')}`;
                mainEntries.push({ type: 'URLAUB', date: date, allDay: true });
            } else if (holidayMatch && currentYear && currentMonth) {
                const [_, day] = holidayMatch;
                const date = `${currentYear}-${currentMonth.padStart(2, '0')}-${day.padStart(2, '0')}`;
                mainEntries.push({ type: 'FEIERTAG', date: date, allDay: true });
            }
        }
    }

    if (!currentYear || !currentMonth) {
        console.error("Could not determine month and year from PDF text.");
        return { entries: [], year: null, month: null };
    }

    // Log the raw extracted entries before processing
    console.log("Raw Main Entries:", JSON.stringify(mainEntries, null, 2));
    console.log("Raw Bereitschaft Entries:", JSON.stringify(bereitschaftEntries, null, 2));

    // --- Pass 2: Process entries, handle merges, and build final list ---
    const finalEntries = [];
    const handledMainIndices = new Set();
    const handledBereitschaftIndices = new Set();

    // Prioritize finding B38 pairs and merging with M3
    for (let i = 0; i < bereitschaftEntries.length; i++) {
        if (handledBereitschaftIndices.has(i)) continue;

        const item1 = bereitschaftEntries[i];

        // Look for B38 start (19:50 - 00:00)
        if (item1.start === '19:50' && item1.end === '00:00') {
            // Search for the corresponding end part (00:00 - 07:35 on the next day)
            for (let j = 0; j < bereitschaftEntries.length; j++) {
                if (i === j || handledBereitschaftIndices.has(j)) continue;

                const item2 = bereitschaftEntries[j];
                if (item2.start === '00:00' && item2.end === '07:35') {
                    // Check if dates are consecutive
                    const startDateObj = new Date(item1.date);
                    startDateObj.setDate(startDateObj.getDate() + 1);
                    const endDateCheckObj = new Date(item2.date);

                    if (startDateObj.toISOString().split('T')[0] === endDateCheckObj.toISOString().split('T')[0]) {
                        // B38 pair found. Check for preceding M3.
                        let merged = false;
                        for (let m = 0; m < mainEntries.length; m++) {
                            if (handledMainIndices.has(m)) continue;
                            const mainEntry = mainEntries[m];
                            if (mainEntry.type === 'M3' && mainEntry.date === item1.date) {
                                // Merge found! Create MO entry.
                                finalEntries.push({
                                    type: 'MO',
                                    date: item1.date, // Start date
                                    start: '11:35',    // Fixed MO start
                                    end: '07:35'     // Fixed MO end
                                });
                                console.log(`Merged M3 and B38 into MO for date: ${item1.date}`);
                                handledMainIndices.add(m); // Mark M3 as handled
                                handledBereitschaftIndices.add(i); // Mark B38 part 1 handled
                                handledBereitschaftIndices.add(j); // Mark B38 part 2 handled
                                merged = true;
                                break; // Found M3, stop searching mainEntries
                            }
                        }

                        if (!merged) {
                            // No M3 found, create separate B38 entry
                            finalEntries.push({
                                type: 'B38',
                                date: item1.date, // Start date
                                start: '19:50',    // B38 start
                                end: '07:35'     // B38 end
                            });
                            console.log(`Added separate B38 for date: ${item1.date} (no preceding M3)`);
                            handledBereitschaftIndices.add(i); // Mark B38 part 1 handled
                            handledBereitschaftIndices.add(j); // Mark B38 part 2 handled
                        }
                        // Whether merged or not, we've handled this B38 pair, break inner Bereitschaft loop
                        break;
                    }
                }
            }
        }
    }

    // Add any unhandled main entries
    for (let i = 0; i < mainEntries.length; i++) {
        if (!handledMainIndices.has(i)) {
            finalEntries.push(mainEntries[i]);
        }
    }

    // Add any unhandled Bereitschaft entries (e.g., B36 or orphaned parts)
    for (let i = 0; i < bereitschaftEntries.length; i++) {
        if (!handledBereitschaftIndices.has(i)) {
            const item = bereitschaftEntries[i];
             // Check for B36
             if (item.start === '07:35' && item.end === '19:35') {
                 finalEntries.push({
                     type: 'B36',
                     date: item.date,
                     start: item.start,
                     end: item.end
                 });
                 console.log(`Added B36 for date: ${item.date}`);
             } else {
                 // Handle other orphans - log warning and add generically
                 console.warn(`Orphaned/Unhandled Bereitschaft entry found: Date=${item.date}, Start=${item.start}, End=${item.end}. Adding as raw.`);
                 finalEntries.push({
                     type: `BEREIT_${item.start}-${item.end}`,
                     date: item.date,
                     start: item.start,
                     end: item.end
                 });
             }
             // Mark as handled to avoid adding again if logic expands
             handledBereitschaftIndices.add(i);
        }
    }

    // Sort final entries by date (optional, but good practice)
    finalEntries.sort((a, b) => new Date(a.date) - new Date(b.date));


    return { entries: finalEntries, year: currentYear, month: currentMonth };
}


// Helper function to format date and time for Google Calendar
function formatForGoogleCalendar(entry, year, month) { // Takes year/month now
    const timeZone = 'Europe/Berlin';
    const workflowId = `dienstplan-${year}-${month.padStart(2, '0')}`; // Pad month here for ID

    const eventBase = {
        timeZone: timeZone,
        extendedProperties: {
            private: {
                n8nWorkflowId: workflowId // Unique identifier
            }
        }
    };

    // Handle all-day events
    if (entry.allDay) {
        return {
            ...eventBase,
            summary: entry.type,
            start: entry.date,
            end: entry.date,
        };
    }

    // Handle regular timed events
    const fullDate = entry.date; // YYYY-MM-DD
    let startDate = new Date(`${fullDate}T${entry.start}:00`); // Assume local time initially
    let endDate = new Date(`${fullDate}T${entry.end}:00`);     // Assume local time initially

    // Handle overnight shifts (B38, MO, or if end time is numerically <= start time)
    // Note: B36 (07:35-19:35) does not cross midnight
    if (entry.type === 'MO' || entry.type === 'B38' || (!entry.allDay && endDate <= startDate)) {
         endDate.setDate(endDate.getDate() + 1);
    }

    // Construct summary
    let summary = entry.type;
    // Explicitly format MO summary, otherwise use standard format
    if (entry.type === 'MO') {
        summary = `MO 11:35-07:35`; // Use the defined MO times
    } else if (entry.type === 'B38') {
        summary = `B38 19:50-07:35`; // Use the defined B38 times
    } else if (!entry.allDay) {
        summary = `${entry.type} ${entry.start}-${entry.end}`;
    }
    // All-day summaries are just the type (handled above)


    return {
        ...eventBase,
        summary: summary,
        start: startDate.toISOString(), // Output UTC ISO string
        end: endDate.toISOString(),     // Output UTC ISO string
    };
}

