    if (!pdfData || !pdfData.text) {
        console.error("Error: pdfData or pdfData.text is missing or undefined.");
        return { json: { error: "Invalid input data" } };
    }

    const { entries: parsedEntries, year: currentYear, month: currentMonth } = parseTimeSheet(pdfData); // month is not padded here
    if (!currentYear || !currentMonth) {
        console.error("Failed to extract year or month. Cannot proceed.");
        return { json: { error: "Could not extract year/month from PDF." } };
    }

    // Calculate start and end of the month for the search
    const yearInt = parseInt(currentYear, 10);
    const monthInt = parseInt(currentMonth, 10); // Month is 1-based from PDF
    const startDateOfMonth = new Date(Date.UTC(yearInt, monthInt - 1, 1, 0, 0, 0));
    const firstOfNextMonth = new Date(Date.UTC(yearInt, monthInt, 1, 0, 0, 0));
    const startDateOfMonthISO = startDateOfMonth.toISOString();
    const endDateOfMonthISO = firstOfNextMonth.toISOString();

    // Define the property key and value for searching
    const paddedMonth = currentMonth.padStart(2, '0');
    const extendedPropertyKey = "n8nWorkflowId";
    const extendedPropertyValue = `dienstplan-${currentYear}-${paddedMonth}`;
    console.log(`Processing PDF for ${currentYear}-${paddedMonth}`);
    console.log(`Search Property: ${extendedPropertyKey}=${extendedPropertyValue}`);
    console.log(`Search Start Date: ${startDateOfMonthISO}`);
    console.log(`Search End Date: ${endDateOfMonthISO}`);

    // Format entries using the updated function
    const eventsToCreate = parsedEntries.map(entry => formatForGoogleCalendar(entry, currentYear, currentMonth)); // Pass raw month
    console.log(`Prepared ${eventsToCreate.length} events to potentially create (after merge).`);
    console.log("Events:", JSON.stringify(eventsToCreate, null, 2)); // Log the events being created

