        json: {
            eventsToCreate: eventsToCreate,
            startDateOfMonth: startDateOfMonthISO,
            endDateOfMonth: endDateOfMonthISO,
            extendedPropertyKey: extendedPropertyKey,
            extendedPropertyValue: extendedPropertyValue,
            year: currentYear,
            month: paddedMonth
        }
    };
});

